import React,{useState} from 'react'
import {Modal} from 'react-bootstrap';
import EditUserForm from './EditUserForm';



function UserItems(props) {
    const [isModalVisible, setShowModal] =useState(false);

    function showModal() {
        setShowModal(true);
    }

    function hideModal() {
        setShowModal(false);
    }
    return (
        <div>
            <div className='row'>
                <div className='col-md-3'>{props.user.username}</div>
                <div className='col-md-3'>{props.user.email}</div>
                <div className='col-md-2'>{props.user.country}</div>
                <div className='col-md-4'>
                    <  button className='del' 
                    onClick={() => {
                        props.delete(props.user.id);
                    }}> Delete</button>



                    <button className='up'>Edit</button>

                    <Modal show={isModalVisible} onHide={hideModal}>
                        <Modal.Header closeButton>
                            <Modal.Tittle>Edit User</Modal.Tittle>
                        </Modal.Header>
                        <Modal.Body>
                            <EditUserForm 
                            user = {props.user}
                            upateUser={props.updateUser}
                            hideModal={hideModal}/>
                        </Modal.Body>
                    </Modal>
                </div>
            </div>
        </div>
    )
};

export default UserItems;
